    package main

    import "golang.org/x/tour/tree"

    func Walk(t *tree.Tree, ch chan int){
    	if t.Left != nil{
    		Walk(t.Left, ch)
    	}
    	ch <- t.Value
    	if t.Right != nil{
    		Walk(t.Right, ch)
    	}
    }

    // Same  t1  t2 
    func Same(t1, t2 *tree.Tree) bool{
    	ch1,ch2 := make(chan int),make(chan int)
    	go func(){
    		Walk(t1, ch1)
    		close(ch1)
    	}()
    	go func(){
    		Walk(t2, ch2)
    		close(ch2)
    	}()
    	for i := range ch1{
    		if i != <-ch2 {
    			return false
    		}
    	}
    	return true
    }

    func main() {
    	println(Same(tree.New(1),tree.New(1)))
    }
