    [https://examples.javacodegeeks.com/core-java/util/concurrent/locks-concurrent/condition/java-util-concurrent-locks-condition-example/](java-concurrent//examples.javacodegeeks.com/core-java/util/concurrent/locks-concurrent/condition/java-util-concurrent-locks-condition-example/)

    ```java
    // SharedFifoQueue.java
    public class SharedFifoQueue {

      private final Object[] content;
      // 
      private int current = 0;
      private int addIndex = 0;
      private int removeIndex = 0;

      private final ReentrantLock lock = new ReentrantLock();
      private final Condition fullCondition = lock.newCondition();
      private final Condition emptyCondition = lock.newCondition();

      public SharedFifoQueue(int capacity) {
          content = new Object[capacity];
      }

      public void add(Object item) throws InterruptedException {
          lock.lock();
          while (current >= content.length) {
              fullCondition.await();
          }
          content[addIndex] = item;
          addIndex = (addIndex + 1) % content.length;
          current++;
          emptyCondition.signal();
          lock.unlock();
      }

      public Object remove() throws InterruptedException {
          lock.lock();
          while (current <= 0) {
              emptyCondition.await();
          }
          Object item = content[removeIndex];
          removeIndex = (removeIndex + 1) % content.length;
          current--;
          fullCondition.signal();
          lock.unlock();
          return item;
      }
    }

    // Producer.java
    public class Producer implements Runnable {

      private final SharedFifoQueue queue;
      private final Random random = new Random();

      public Producer(SharedFifoQueue queue) {
          this.queue = queue;
      }

      @Override
      public void run() {
          try {
              for (int i = 0; i < 20; i++) {
                  queue.add("Hello " + i);
                  Thread.sleep(random.nextInt(200) );
              }
              queue.add(null);
          } catch (InterruptedException e) {
              e.printStackTrace();
          }
      }
    }

    // Consumer.java
    public class Consumer implements Runnable {

      private final SharedFifoQueue queue;

      public Consumer(SharedFifoQueue queue) {
          this.queue = queue;
      }

      @Override
      public void run() {
          try {
              while (true) {
                  Object item = queue.remove();
                  if (item == null) {
                      break;
                  }
                  System.out.println(item.toString());
              }
          } catch (InterruptedException e) {
              e.printStackTrace();
          }
      }
    }

    // Main.java
    public static void main(String[] args) throws InterruptedException {
        SharedFifoQueue sharedFifoQueue = new SharedFifoQueue(10);
        Thread consumerThread = new Thread(new Consumer(sharedFifoQueue));
        Thread producerThread = new Thread(new Producer(sharedFifoQueue));

        producerThread.start();
        consumerThread.start();
        // join 
        producerThread.join();
        consumerThread.join();
    }
    ```

