public static void main(String[] args) {
  String s1 = "Hello";
  String s2 = "World";
  swap(s1, s2);
  System.out.printf("main s1: %s, s2: %s%n", s1, s2);
}

static void swap(String s1, String s2) {
  String tmp = s1;
  s1 = s2;
  s2 = tmp;
  System.out.printf("swap s1: %s, s2: %s%n", s1, s2);
}
// 
// swap s1: World, s2: Hello
// main s1: Hello, s2: World
