    ```java
    public class MultiplexerNioServer implements Runnable {

        private Selector selector;
        private volatile boolean stop = false;

        /**
         *  
         *
         * @param port
         */
        public MultiplexerNioServer(int port) {
            try {
                ServerSocketChannel serverSocketChannel = ServerSocketChannel.open();//serverChannel
                selector = Selector.open();////  
                serverSocketChannel.configureBlocking(false);//  true false
                serverSocketChannel.socket().bind(new InetSocketAddress(port), 1024);//
                serverSocketChannel.register(selector, SelectionKey.OP_ACCEPT);//selectorchannel
            } catch (IOException e) {
                e.printStackTrace();
                System.exit(1);
            }
        }

        public void stop() {
            this.stop = true; // 
        }

        public void run() {
            while (!stop) {
                try {
                    //selector1s
                    int client = selector.select(1000);
                    System.out.println("1:" + client);
                    // ,.
                    // int client = selector.select(); IO
                    if (client == 0) {
                        continue;
                    }
                    System.out.println("2:" + client);
                    Set<SelectionKey> selectionKeys = selector.selectedKeys();
                    Iterator<SelectionKey> it = selectionKeys.iterator();
                    SelectionKey key = null;
                    while (it.hasNext()) {
                        key = it.next();
                        it.remove();
                        try {
                            //
                            handle(key);
                        } catch (Exception e) {
                            if (key != null) {
                                key.cancel();
                                if (key.channel() != null) {
                                    key.channel().close();
                                }
                            }
                        }
                    }
                } catch (Throwable e) {
                    e.printStackTrace();
                }
            }

            if (selector != null) {
                // selector
                try {
                    selector.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }

        public void handle(SelectionKey key) throws IOException {
            if (key.isValid()) {
                //
                if (key.isAcceptable()) {
                    ServerSocketChannel ssc = (ServerSocketChannel) key.channel();
                    // ServerSocketChannelacceptSocketChannel
                    // TCPTCP
                    SocketChannel sc = ssc.accept();//3
                    sc.configureBlocking(false);
                    sc.register(selector, SelectionKey.OP_READ);//
                }

                //
                if (key.isReadable()) {
                    SocketChannel socketChannel = (SocketChannel) key.channel();
                    ByteBuffer readbuffer = ByteBuffer.allocate(1024);// 0 1024  1024
    //                ByteBuffer readbuffer = ByteBuffer.allocateDirect(1024); //
                    // 
                    while (true) {
                        int readBytes = socketChannel.read(readbuffer);
                        // 
                        if (readBytes > 0) {
                            // limitposition=0
                            readbuffer.flip();//
                            // 
                            byte[] bytes = new byte[readbuffer.remaining()];
                            readbuffer.get(bytes);
                            String body = new String(bytes, StandardCharsets.UTF_8);
                            System.out.println("input is:" + body);
                            res(socketChannel, body);
                        } else if (readBytes < 0) {
                            //  
                            key.cancel();
                            socketChannel.close();
                        } else {
                            // 
                            return;
                        }
                    }

                }

            }
        }

        private void res(SocketChannel channel, String response) throws IOException {
            if (response != null && response.length() > 0) {
                byte[] bytes = response.getBytes();
                ByteBuffer writeBuffer = ByteBuffer.allocate(bytes.length);
                writeBuffer.put(bytes);
                writeBuffer.flip();
                channel.write(writeBuffer);
                System.out.println("res end");
            }
        }
    }
    ```

- NIO 

    ```java
    public class NioClientHandler implements Runnable {
        private final String host;
        private final int port;
        private Selector selector;
        private SocketChannel socketChannel;
        private volatile boolean stop;

        public NioClientHandler(String host, int port) {
            this.host = host;
            this.port = port;
            try {
                // 
                selector = Selector.open();
                // 
                socketChannel = SocketChannel.open();
                //  true false
                socketChannel.configureBlocking(false); // 
            } catch (IOException e) {
                e.printStackTrace();
                System.exit(1);
            }
        }

        public void run() {
            try {
                doConnect();
            } catch (IOException e) {
                e.printStackTrace();
                System.exit(1);
            }
            while (!stop) {
                try {
                    int wait = selector.select(1000);
                    if (wait == 0) {
                        continue;
                    }
                    Set<SelectionKey> selectionKeys = selector.selectedKeys();
                    Iterator<SelectionKey> it = selectionKeys.iterator();
                    SelectionKey key = null;

                    while (it.hasNext()) {
                        key = it.next();
                        it.remove();
                        try {
                            handle(key);
                        } catch (Exception e) {
                            if (key != null) {
                                key.cancel();
                                if (key.channel() != null) {
                                    key.channel().close();
                                }
                            }
                        }
                    }
                } catch (Exception e) {
                    e.printStackTrace();
                    System.exit(1);
                }
            }
            if (selector != null) {
                try {
                    selector.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }

        private void doConnect() throws IOException {
            if (socketChannel.connect(new InetSocketAddress(host, port))) {
                socketChannel.register(selector, SelectionKey.OP_READ);
                doWrite(socketChannel);
            } else {
                socketChannel.register(selector, SelectionKey.OP_CONNECT);
            }
        }

        private void handle(SelectionKey key) throws IOException {
            if (key.isValid()) {
                SocketChannel sc = (SocketChannel) key.channel();
                if (key.isConnectable()) {
                    if (sc.finishConnect()) {
                        sc.register(selector, SelectionKey.OP_READ);
                        doWrite(sc);
                    } else {
                        System.exit(1);
                    }
                }
                if (key.isReadable()) {
                    ByteBuffer readBuffer = ByteBuffer.allocate(1024);
                    int readBytes = sc.read(readBuffer);
                    if (readBytes > 0) {
                        readBuffer.flip();
                        byte[] bytes = new byte[readBuffer.remaining()];
                        readBuffer.get(bytes);
                        String body = new String(bytes, StandardCharsets.UTF_8);
                        System.out.println("res" + body);
                        this.stop = true;
                    } else if (readBytes < 0) {
                        key.cancel();
                        sc.close();
                    }

                }
            }
        }

        private void doWrite(SocketChannel sc) throws IOException {
            // 
            byte[] request = "Hello".getBytes();
            // ByteBuffer
            ByteBuffer writeBuffer = ByteBuffer.allocate(request.length);
            // 
            writeBuffer.put(request);
            // flip
            writeBuffer.flip();
            sc.write(writeBuffer);
            if (!writeBuffer.hasRemaining()) {
                System.out.println("");
            }
        }
    }
    ```

