        private final Data data;

        public Receiver(Data data) {
            this.data = data;
        }

        @Override
        public void run() {
            String packet = data.receive();
            while (!"Over".equals(packet)) {
                System.out.println(packet);
                packet = data.receive();
            }
        }
    }

    // Main.java
